import React from "react";
import "./style.css";

class ImageCard extends React.Component {
  state = {
    count: 0
  };


  handleIncrement = (props) => {
    this.setState({ count: this.StaticRange.count + 1 });
  };

  render(props) {
    return (
      <div className="imgContainer">
        <img alt={props.id} src={props.image} data-clicked={props.clicked} onClick={this.handleIncrement} />
      </div>
    );
  }
}
export default ImageCard;
