import ImageCard from "../ImageCard";

class Counter extends React.Component {
    state = {
        count: 0
    };

    handleIncrement = () => {
        this.setState({ count: this.state.count + 1 });
    };


    render() {
        return (
            <p className="lead">Score: {props.count} | High Score: {props.highScore}</p>
        )
    }
}

export default Counter;
